DELIMITER ;;

CREATE TRIGGER `deletePoints` BEFORE DELETE ON `questionnaire` FOR EACH ROW
BEGIN

UPDATE user AS u JOIN (
    SELECT user_fk,
    CASE WHEN sex<>'U' THEN 2 ELSE 0 END
    + CASE WHEN age<>0 THEN 2 ELSE 0 END
    + CASE WHEN expertise<>'Unknown' THEN 2 ELSE 0 END
    + (SELECT COUNT(*) FROM answer 
       WHERE access_fk=a.access_id) AS points
    FROM access AS a WHERE access_id in 
    (SELECT access_id where quest_fk=old.quest_id)
) AS p ON user_fk=user_id SET u.points=u.points-p.points;

END;;

DELIMITER ;

